    private void tblInvoiceMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblInvoiceMouseClicked
        // TODO add your handling code here:
        lblStaffId.setText(tblInvoice.getValueAt(tblInvoice.getSelectedRow(), 0).toString());
        txtFirstName.setText(tblInvoice.getValueAt(tblInvoice.getSelectedRow(), 1).toString());
        txtSurname.setText(tblInvoice.getValueAt(tblInvoice.getSelectedRow(), 2).toString());
        txtEmail.setText(tblInvoice.getValueAt(tblInvoice.getSelectedRow(), 3).toString());
        cbJobTitle.getModel().setSelectedItem(tblInvoice.getValueAt(tblInvoice.getSelectedRow(), 4).toString());
    }//GEN-LAST:event_tblInvoiceMouseClicked

